const initialState = {
    data: [] ,
    showLoading : false
};
export function ReduxProduct(state = initialState, action) {
    switch (action.type) {
        case "API_CALL_SUCCESS":
            return {
                data: action.payload
            };
        default: 
            return state
    }
}

