import { useState } from "react";
import { useParams } from "react-router-dom";
import Data from "../db/products.json"
function DetailsPage() {
    const {detailsId} = useParams()
    const [indexItem , setIndexItem] = useState(1)
    const newProduct = [...Data].filter((item,index)=>{
        return item.productId == detailsId;
       })
       const handleMinus = () =>{
        // indexItem > 2 ? setIndexItem(indexItem--) : alert('Ko thể giảm')
       }
       const handlePlus = () =>{
        // indexItem < 99 ? setIndexItem(indexItem++) : alert('Ko thể tăng ')
       }
    return ( 
        <>
            {newProduct.map(val =>{ 
                return(
                <div className="bg-slate-50 flex flex-col my-4 mr-2"
                    key={val.productId}>
                    <img src={val.imageUrl}/>
                    <h3 className='font-bold'>
                                {val.productName}
                    </h3>
                    <p>
                        {val.description}
                    </p>
                    <div className="flex justify-around">
                        <div className="flex rounded-md items-center"
                        style={{backgroundColor :'#e5e7eb'}}>
                            <button onClick={handleMinus} className='w-8'>
                                <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" className="bi bi-dash" viewBox="0 0 16 16">
                                    <path d="M4 8a.5.5 0 0 1 .5-.5h7a.5.5 0 0 1 0 1h-7A.5.5 0 0 1 4 8z"/>
                                </svg>
                            </button>
                            <span className='w-8 text-danger'>{indexItem}</span>
                            <button onClick={handlePlus} className='w-8'>
                            <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" className="bi bi-plus-lg" viewBox="0 0 16 16">
                                <path fill-rule="evenodd" d="M8 2a.5.5 0 0 1 .5.5v5h5a.5.5 0 0 1 0 1h-5v5a.5.5 0 0 1-1 0v-5h-5a.5.5 0 0 1 0-1h5v-5A.5.5 0 0 1 8 2Z"/>
                            </svg>
                            </button>
                        </div>
                        <h3 className='font-bold'>
                                {val.price}
                        </h3>

                    </div>
                </div>
                )
            })}
        </>
     );
}

export default DetailsPage;
