import { useEffect, useRef, useState } from 'react';
import GetImg from '../../Img/shoppingcart-136-default-rtdb-export.json'
import './Style.css'
const delay = 2000;
function SlideShow() {
    const [index , setIndex] = useState(0);
    const timeoutRef = useRef(null);
    function resetTimeout() {
        if (timeoutRef.current) {
          clearTimeout(timeoutRef.current);
        }
      }
      useEffect(() => {
        resetTimeout();
        timeoutRef.current = setTimeout(
          () =>
            setIndex((prevIndex) =>
            GetImg.Products.map((Items) =>{ 
                return( prevIndex === Items.length - 1 ? 0 : prevIndex + 1)
            })
             
            ),
          delay
        );
    
        return () => {
          resetTimeout();
        };
      }, [index]);
    
    return (  <div className="slideshow">
    <div
      className="slideshowSlider"
      style={{ transform: `translate3d(${-index * 100}%, 0, 0)` }}
    >
      {GetImg.Products.map((item, index) => (
        <div
          className="slide"
          key={index}
        > <img src={item.imageUrl} className='w-80'/></div>
      ))}
    </div>

    <div className="slideshowDots">
      {GetImg.Products.map((_, idx) => (
        <div
          key={idx}
          className={`slideshowDot${index === idx ? " active" : ""}`}
          onClick={() => {
            setIndex(idx);
          }}
        ></div>
      ))}
    </div>
  </div> );
}

export default SlideShow;
