import axios from "axios";
import {
  takeLatest,
  call,
  put
} from "redux-saga/effects";
import reduxSagaFirebase from '../Firebase/FbSaga'
import firebase from 'firebase'
import '@firebase/firestore' // ðŸ‘ˆ If you're using firestore
import ReduxSagaFirebase from 'redux-saga-firebase'

const authProvider = new firebase.auth.GoogleAuthProvider()
function* loginSaga() {
     const data = yield call(reduxSagaFirebase.auth.signInWithPopup, authProvider)
    yield put({
      type: "API_CALL_SUCCESS",
      payload: data
    })

}

async function fetchData() {
  let response = await axios.get(`https://639af01231877e43d67c3261.mockapi.io/api/products`);
  let products = await response.data;
  return products;
}

function* workerSaga() {
  const data = yield call(fetchData);
  yield put({
    type: "API_CALL_SUCCESS",
    payload: data
  });

}
// export function* mySaga(){
//   yield takeLatest('GET_API',workerSaga);
// }
export function* mySaga(){
  yield takeLatest('GET_API',loginSaga);
}


