import {
  GET_NUMBER_CART,
  ADD_CART,
} from "../Actions/Actions";
const initialState = {
  data: [],
  numberCart: 0,
  Carts: [],
  count : 1,
};

export  function ReduxProduct(state = initialState, action) {
  switch (action.type) {
    case "API_CALL_SUCCESS":
      return {
        ...state,
        data: action.payload,
      };

    case GET_NUMBER_CART:
      return {
        ...state,
      };
    case ADD_CART:
      if (state.numberCart == 0) {
        return {
          ...state,
          numberCart: state.numberCart + 1,
        };}
      
   
    default:
      return state;
  }
}
