import { useEffect, useState } from 'react';
import { NavLink, Outlet } from 'react-router-dom';
import DetailsPage from './Details-Page';
import axios from 'axios';
import GetAPI from '../db/GetAPI';
function ProductPage({callAPI}) {
    console.log(callAPI)
    const [getProducts , setGetProducts] = useState([])
    const url = `http://localhost:4000/api/products`
async function fetchData() {
    let response = await axios(url);
    let products = await response.data;
    setGetProducts(products);
}
useEffect(() =>{
    fetchData();
  },[])
    return ( 
        <div className ='grid grid-cols-2 py-16 px-16 '
            style={{backgroundColor :'#e5e7eb'}}>
            <div>
                <GetAPI />
                <DetailsPage/>
            </div>
            <div>
            {getProducts.map(value  =>{
                return(
                    <div className='grid grid-cols-2 w-full h-44 bg-slate-50 my-4 '
                        key={value.productId}>
                        <div>
                            <img src={value.imageUrl} className= 'h-36'/>
                        </div>
                        <div className='flex flex-col'>
                            <h3 className='font-extrabold'>
                                {value.productName}
                            </h3>
                            <p>
                                {value.description}
                            </p>
                            <div className='flex justify-between my-8'>
                            <h3 className='font-extrabold'>
                                {value.price}
                            </h3>
                            <NavLink to ={`/products/${value.productId}`}>Details</NavLink>
                            </div>
                        </div> 
                    </div>
                )
            })}
            </div>
        </div>
     );
}

export default ProductPage;
