import { useEffect, useState } from "react";
import { AppFb } from "../../Firebase/Firebase";
import StyledFirebaseAuth from 'react-firebaseui/StyledFirebaseAuth';
// Configure FirebaseUI.
const uiConfig = {
    // Popup signin flow rather than redirect flow.
    signInFlow: 'popup',
    // We will display Google and Facebook as auth providers.
    signInOptions: [
        AppFb.auth.GoogleAuthProvider.PROVIDER_ID,
    ],
    callbacks: {
      // Avoid redirects after sign-in.
      signInSuccessWithAuthResult: () => false,
    },
  };
function AuthLogin() {
    const [isSignedIn, setIsSignedIn] = useState(false); // Local signed-in state.

  // Listen to the Firebase Auth state and set the local state.
  useEffect(() => {
    const unregisterAuthObserver = AppFb.auth().onAuthStateChanged(async (user) => {
        if(!user){
            return ;
        }
        console.log('Logged in user: ',user.displayName)
        const token = await user.getIdToken();
        console.log('Logged in user: ',token)
      setIsSignedIn(!!user);
    });
    return () => unregisterAuthObserver(); // Make sure we un-register Firebase observers when the component unmounts.
  }, []);
  if (!isSignedIn) {
    return (
      <div>
        <h1>My App</h1>
        <p>Please sign-in:</p>
        <StyledFirebaseAuth uiConfig={uiConfig} firebaseAuth={AppFb.auth()} />
      </div>
    );
  }
    return ( 
        <div>
        <h1>My App</h1>
        <p>Welcome {AppFb.auth().currentUser.displayName}! You are now signed-in!</p>
        <a onClick={() => AppFb.auth().signOut()}>Sign-out</a>
      </div>
     );
}

export default AuthLogin;
