import { useState } from "react";
import { useParams } from "react-router-dom";
import CustomButton from "../Component/Button/Custom-button";
import { useDispatch, useSelector } from 'react-redux';
import { INCREASE , DECREASE } from "../db/Redux/Actions/Actions";
function DetailsPage() {
    const [getProducts , setGetProducts] = useState([])
    const dispatch = useDispatch()
    const {detailsId} = useParams()
    const [indexItem , setIndexItem] = useState(1)
    const [disable , setDisable] = useState({plus:false,minus:false})
    const data = useSelector(state => state.ReduxProduct)
    const counter = useSelector(state => state.count )
    console.log(counter)

    const newProduct = [...data.data].filter((item,index)=>{
        return item.productId == detailsId;
       })
       const handleMinus = () =>{
            if(indexItem > 1){
                setIndexItem(prev => prev -1 )
                setDisable({...disable ,minus :false , plus :false})
            }else{
                setDisable({...disable ,minus :true})
            }
       }
       const handlePlus = () =>{
        if(indexItem < 99){
            setIndexItem(prev => prev + 1)
            setDisable({...disable ,plus :false, minus:false})
        }
        else{
            setDisable({...disable ,plus :true})
        }
       }
    return ( 
        <>
            {newProduct.map(val =>{ 
                return(
                <div className="bg-slate-50 flex flex-col my-4 mr-2"
                    key={val.productId}>
                <div className="mx-8">
                    <img src={val.imageUrl}/>
                    <h3 className='font-extrabold'>
                                {val.productName}
                    </h3>
                    <p>
                        {val.description}
                    </p>
                    <div className="flex justify-around my-8">
                        <div className="flex rounded-md items-center"
                        style={{backgroundColor :'#e5e7eb'}}>
                            <CustomButton onClick={handleMinus}
                                            className = 'w-8'
                                            disabled ={disable.minus}
                                            value = '-' />
                            <span  className='w-4 '>{counter}</span>
                            <CustomButton onClick={handlePlus}
                                            className='w-8'
                                            disabled ={disable.plus}
                                            value = '+'/>
                        </div>
                        <div className="flex">
                            <h3 className='font-bold mr-4'>
                                {val.price}
                            </h3>
                            <CustomButton className="rounded-lg bg-blue-500 text-white w-36 h-8 flex justify-center items-center"
                                            value = 'Add to cart'     />
                        </div>
                    </div>
                    </div>
                </div>
                )
            })}
        </>
     );
}

export default DetailsPage;
