//     });
//     if(data.val() !== null){
//       yield put({
//         type: "API_CALL_SUCCESS",
//         payload: data
//       })
//     }
//   }catch (error) {

//   }
// }
function* workerSaga() {
  const data = yield call(FetchFb);
  yield put({
    type: "API_CALL_SUCCESS",
    payload: data
  });
}

export function* mySaga(){
  yield takeLatest('GET_API',workerSaga);
}
