import { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import CustomButton from '../Component/Button/Custom-button'
import { DeleteCart , DECRE, INCRE } from "../db/Redux/Actions/Actions";
function PaymentPage() {
    const items = useSelector(state => state.ReduxProduct.Carts);
    const count = useSelector(state => state.ReduxProduct.count)
    const [counts , setCounts ] = useState()
    const dispatch = useDispatch()
    const handleRemove = (index) =>{
        dispatch(DeleteCart(index))
    }
    const handlePlus = () =>{
            dispatch(INCRE())
            // setCounts(quantity+1)
            
       }
    //    console.log(counts)
       const handleMinus = () =>{

            dispatch(DECRE())

   }
   const priceItems = (price ,quantity) =>{
       const total = price * quantity ;
       return total
   }
    return (
        <div className="col-span-2 flex flex-col justify-center gap-4">
        {items.map((value , index) =>{
            return(
                
                <div className="bg-white rounded-lg grid grid-cols-3 mx-9 p-4"
                        key={index}>
                    <div> 
                    <img src={value.image}/>
                    </div>
                    <div className="col-span-2 mx-2 ">
                        <div className="flex justify-around">
                            <p className=" font-semibold text-2xl">
                                {value.name}
                            </p>
                        <CustomButton onClick={() => handleRemove(index)}
                                            className = 'w-8'
                                            value = 'xoa' />
                                            
                        </div>
                        <div className="my-4">
                            <p>{value.description}</p>
                        </div>
                        <div className="flex justify-around "
                           >
                            <div style={{backgroundColor :'#e5e7eb'}}>
                            <CustomButton onClick={handleMinus}
                                            className = 'w-8 text-red-600'
                                            value = '-' />
                            <span  className='w-4 '>{value.quantity}</span>
                            <CustomButton onClick={handlePlus(value.quantity)}
                                            className='w-8 text-red-600'
                                            value = '+'/>
                            </div>
                            <p className=" font-semibold text-2xl">
                                {priceItems(value.price , value.quantity)}
                            </p>
      
                        </div>

                    </div>
                </div>
            )
        })}
      </div>
)
    }
export default PaymentPage;
