import { useEffect, useState } from "react";
import { useSelector } from "react-redux";
import { Outlet, useNavigate } from "react-router-dom";

function CheckoutPage() {
    let navigate = useNavigate();
    const items = useSelector(state => state.ReduxProduct.Carts)
    const [disable , setDisable] = useState(true)
    useEffect(() =>{
         if(items.length > 0){
        setDisable(false)
    }
    },[items.length])
   
    console.log(items.length)
    const handleCheckout = () =>{
        console.log('Clicked!!!!')
    }
    return ( 
        <div className=" w-screen h-full flex flex-col py-14 "
                style={{backgroundColor :'#e5e7eb'}}>
            <div className="bg-white rounded-lg h-14  mx-9 justify-center font-semibold flex items-center mt-5">
                    <p> My Shopping Cart</p>   
            </div>
            
            <div className="grid grid-cols-3 gap-4 mt-4 w-">
                {items.length == 0 ? 
                <div className="col-span-2 flex justify-center ">You have no products in cart</div>:
                <Outlet/>}
                <div className="space-y-4">
                <div className="bg-white rounded-lg flex flex-col mx-9 p-4">
                    <p className=" font-semibold text-2xl">Order Info</p>
                    <div className="grid grid-cols-2 gap-4 pt-3">
                        <div>
                            <p>Subtotal:</p>
                            <p>Shipping Cost:</p>
                        </div>
                        <div>
                            <p>$0</p>
                            <p>$10</p>
                        </div>
                    </div>
                    <div className="grid grid-cols-2 gap-4 pt-6 font-bold text-2xl">
                        <p>Total:</p>
                        <p>$0.00</p>
                    </div>
                </div>
                <div className="flex flex-col gap-4 h-20 mx-9  ">
                <button  className="h-14"
                            disabled ={disable}
                            onClick = {handleCheckout}
                    style={{backgroundColor :'#3B82F6',
                                    color : 'white'}}>Checkout</button>
                <button className="h-14 border-2 border-sky-500 text-blue-500"
                         onClick={() =>navigate('/products')}>Continue Shopping</button>
                </div>
                </div>
            </div>
        </div>
     );
}

export default CheckoutPage;
