import {
    useState
} from 'react';
import {
    NavLink,
    Outlet
} from 'react-router-dom';
import DetailsPage from './Details-Page';
import GetAPI from '../db/GetAPI';
import {
    ReduxProduct
} from '../db/Redux/ReduxProduct';

function ProductPage() {
    const [getProducts, setGetProducts] = useState([])

    return ( <
        div className = 'grid grid-cols-2 py-16 px-16 '
        style = {
            {
                backgroundColor: '#e5e7eb'
            }
        } >
        <
        div >
        <
        GetAPI / >
        <
        DetailsPage / >
        <
        /div> <
        div > {
            getProducts.map(value => {
                return ( <
                    div className = 'grid grid-cols-2 w-full h-44 bg-slate-50 my-4 '
                    key = {
                        value.productId
                    } >
                    <
                    div >
                    <
                    img src = {
                        value.imageUrl
                    }
                    className = 'h-36' / >
                    <
                    /div> <
                    div className = 'flex flex-col' >
                    <
                    h3 className = 'font-extrabold' > {
                        value.productName
                    } <
                    /h3> <
                    p > {
                        value.description
                    } <
                    /p> <
                    div className = 'flex justify-between my-8' >
                    <
                    h3 className = 'font-extrabold' > {
                        value.price
                    } <
                    /h3> <
                    NavLink to = {
                        `/products/${value.productId}`
                    } > Details < /NavLink> <
                    /div> <
                    /div>  <
                    /div>
                )
            })
        } <
        /div> <
        /div>
    );
}

export default ProductPage;
