import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import CustomButton from "../Component/Button/Custom-button";
import { useDispatch, useSelector } from 'react-redux';
import { ReduxProduct } from "../db/Redux/Reducers/ReduxProduct";
import {AddCart, DECRE, INCRE ,ResetCount} from '../db/Redux/Actions/Actions'
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
function DetailsPage() {
    const dispatch = useDispatch()
    const data = useSelector(state => state.ReduxProduct)
    const {detailsId} = useParams()
    const count = useSelector(state => state.ReduxProduct.count)
    useEffect(()=>{
        dispatch(ResetCount())
    },[detailsId])
    const newProduct = [...data.data].filter((item,index)=>{
        return item.productId == detailsId;
    })
       const handleMinus = () =>{
       
            if(count > 1){
                dispatch(DECRE())
            }else{
                return 1
            }
       }
       const handlePlus = () =>{
       
        if(count < 99){
            dispatch(INCRE())
        }
        else{
           return 99
        }
       }
       const handleAdd = (item) =>{
            dispatch(AddCart(item))
            toast.success('Thank your for purchased!!!', {
                position: toast.POSITION.BOTTOM_LEFT
            });
       }
    return ( 
        <>
            {newProduct.map(item =>{ 
                return(
                <div className="bg-slate-50 flex flex-col my-4 mr-2"
                    key={item.productId}>
                <div className="mx-8">
                    <img src={item.imageUrl}/>
                    <h3 className='font-extrabold'>
                                {item.productName}
                    </h3>
                    <p>
                        {item.description}
                    </p>
                    <div className="flex justify-around my-8">
                        <div className="flex rounded-md items-center"
                        style={{backgroundColor :'#e5e7eb'}}>
                            <CustomButton onClick={handleMinus}
                                            className = 'w-8'
                                            value = '-' />
                            <span  className='w-4 '>{count}</span>
                            <CustomButton onClick={handlePlus}
                                            className='w-8'
                                            value = '+'/>
                        </div>
                        <div className="flex">
                            <h3 className='font-bold mr-4'>
                                {item.price}
                            </h3>
                            <CustomButton className="rounded-lg bg-blue-500 text-white w-36 h-8 flex justify-center items-center"
                                            value = 'Add to cart' 
                                            onClick={() => handleAdd(item)}    />
                                            <ToastContainer />
                        </div>
                    </div>
                    </div>
                </div>
                )
            })}
        </>
     );
}

export default DetailsPage;
