import { useEffect, useRef, useState } from 'react';
import GetImg from '../../Img/shoppingcart-136-default-rtdb-export.json'
function SlideShow() {
   const getUrl = GetImg.Products.map(item =>{
    return item.imageUrl
   })
   const [index , setIndex] = useState(0);
   const timeoutRef = useRef(null);
   function resetTimeout() {
    if (timeoutRef.current) {
      clearTimeout(timeoutRef.current);
    }
  }
  useEffect(() => {
    resetTimeout();
    timeoutRef.current = setTimeout(() =>
        setIndex((prevIndex) =>
          prevIndex === getUrl.length - 1 ? 0 : prevIndex + 1
        ),
      2000
    );

    return () => {
      resetTimeout();
    };
  }, [index]);
    return ( 
        <div className="slideshow">
        <div
          className="slideshowSlider"
          style={{ transform: `translate3d(${-index * 100}%, 0, 0)` }}
        >
          { GetImg.Products.map((images, index) => (
            <div
              className="slide"
              key={index}
              
            ><img src={images.imageUrl}/></div>
          ))}
        </div>
  
        <div className="slideshowDots">
          { GetImg.Products.map((_, idx) => (
            <div
              key={idx}
              className={`slideshowDot${index === idx ? " active" : ""}`}
              onClick={() => {
                setIndex(idx);
              }}
            ></div>
          ))}
        </div>
      </div>
        );
}

export default SlideShow;
