    data: [] ,
    numberCart:0,
    Carts:[],
    showLoading : false
};
export function ReduxProduct(state = initialState, action) {
    console.log(action)
    switch (action.type) {
        case "API_CALL_SUCCESS":
            return {
                data: action.payload
            };
            case "GET_NUMBER_CART":
                                return{
                                    ...state
                                }
