import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import Data from "../db/products.json"
function DetailsPage() {
    const [data , setData] = useState([])
    useEffect(() =>{
        const getPro = async () =>{
            const response = await fetch('http://localhost:4000/api/products')
            
        }
        getPro();
    },[])
    const {detailsId} = useParams()
    const [indexItem , setIndexItem] = useState(1)
    const [disable , setDisable] = useState({plus:false,minus:false})
    const newProduct = [...Data].filter((item,index)=>{
        return item.productId == detailsId;
       })

       const handleMinus = () =>{
            if(indexItem > 1){
                setIndexItem(prev => prev -1 )
                setDisable({...disable ,minus :false , plus :false})
            }else{
                setDisable({...disable ,minus :true})
            }
       }
       const handlePlus = () =>{
        if(indexItem < 10){
            setIndexItem(prev => prev + 1)
            setDisable({...disable ,plus :false, minus:false})
        }
        else{
            setDisable({...disable ,plus :true})
        }
       }
       console.log(indexItem)
    return ( 
        <>
            {newProduct.map(val =>{ 
                return(
                <div className="bg-slate-50 flex flex-col my-4 mr-2"
                    key={val.productId}>
                <div className="mx-8">
                    <img src={val.imageUrl}/>
                    <h3 className='font-extrabold'>
                                {val.productName}
                    </h3>
                    <p>
                        {val.description}
                    </p>
                    <div className="flex justify-around my-8">
                        <div className="flex rounded-md items-center"
                        style={{backgroundColor :'#e5e7eb'}}>
                            <button onClick={handleMinus} className='w-8' disabled ={disable.minus}>
                                <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" className="bi bi-dash font-bold" viewBox="0 0 16 16">
                                    <path d="M4 8a.5.5 0 0 1 .5-.5h7a.5.5 0 0 1 0 1h-7A.5.5 0 0 1 4 8z"/>
                                </svg>
                            </button>
                            <span  className='w-8 '>{indexItem}</span>
                            <button onClick={handlePlus} className='w-8' disabled ={disable.plus}>
                            <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" className="bi bi-plus-lg text-red-500 font-bold" viewBox="0 0 16 16">
                                <path fillRule="evenodd" d="M8 2a.5.5 0 0 1 .5.5v5h5a.5.5 0 0 1 0 1h-5v5a.5.5 0 0 1-1 0v-5h-5a.5.5 0 0 1 0-1h5v-5A.5.5 0 0 1 8 2Z"/>
                            </svg>
                            </button>
                        </div>
                        <div className="flex">
                            <h3 className='font-bold mr-4'>
                                {val.price}
                            </h3>
                            <button className="rounded-lg bg-blue-500 text-white w-36 h-8 flex justify-center items-center">
                                <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" className="bi bi-cart-fill " viewBox="0 0 16 16">
                                        <path d="M0 1.5A.5.5 0 0 1 .5 1H2a.5.5 0 0 1 .485.379L2.89 3H14.5a.5.5 0 0 1 .491.592l-1.5 8A.5.5 0 0 1 13 12H4a.5.5 0 0 1-.491-.408L2.01 3.607 1.61 2H.5a.5.5 0 0 1-.5-.5zM5 12a2 2 0 1 0 0 4 2 2 0 0 0 0-4zm7 0a2 2 0 1 0 0 4 2 2 0 0 0 0-4zm-7 1a1 1 0 1 1 0 2 1 1 0 0 1 0-2zm7 0a1 1 0 1 1 0 2 1 1 0 0 1 0-2z"/>
                                </svg>
                                <p className="ml-4">Add to cart</p>
                            </button>
                        </div>
                    </div>
                    </div>
                </div>
                )
            })}
        </>
     );
}

export default DetailsPage;
