//   let products = await response.data;
//   return products;
// }

function* workerSaga() {
  const data = yield call(fetchData);
  yield put({
    type: "API_CALL_SUCCESS",
    payload: data
  });

}
export function* mySaga(){
  yield takeLatest('GET_API',workerSaga);
}
