import {  useEffect, useState} from 'react';
import { NavLink } from 'react-router-dom';
import DetailsPage from './Details-Page';
import { useDispatch, useSelector   } from 'react-redux';
import Loading from '../Component/Loading/Loading';
function ProductPage() {
    const data = useSelector(state => state.ReduxProduct)
    const dispath = useDispatch()
    useEffect(() => {
        dispath({
            type: "GET_API",
        })
    }, [])
    const products = [...data.data]
    console.log(products)
    return ( 
        <>{data.length = 0 ?
        <div className='flex py-16 items-center justify-center h-screen'
        style={{backgroundColor :'#e5e7eb'}}>
                <Loading/>

        </div>
        :
        <div className ='grid grid-cols-2 py-16 px-16 '
            style={{backgroundColor :'#e5e7eb'}}>
            <div>
                <DetailsPage/>
            </div>
            <div>
            {data.data.map(value  =>{
                return(
                    <div className='grid grid-cols-2 w-full h-44 bg-slate-50 my-4 '
                        key={value.productId}>
                        <div>
                            <img src={value.imageUrl} className= 'h-36'/>
                        </div>
                        <div className='flex flex-col'>
                            <h3 className='font-extrabold'>
                                {value.productName}
                            </h3>
                            <p>
                                {value.description}
                            </p>
                            <div className='flex justify-between my-8'>
                            <h3 className='font-extrabold'>
                                {value.price}
                            </h3>
                            <NavLink to ={`/products/${value.productId}`}>Details</NavLink>
                            </div>
                        </div> 
                    </div>
                )
            })}
            </div>
        </div>}
        </>
     );
}

export default ProductPage;
