         if(items.length > 0){
            setDisable(false) 
        }else{
            setDisable(true)
        }
    },[items.length])
   
    const handleCheckout = () =>{
        console.log('Clicked!!!!')
    }
    return ( 
        <div className=" w-screen h-full flex flex-col py-14 "
                style={{backgroundColor :'#e5e7eb'}}>
            <div className="bg-white rounded-lg h-14  mx-9 justify-center font-semibold flex items-center mt-5">
                    <p> My Shopping Cart</p>   
            </div>
            
            <div className="grid grid-cols-3 gap-4 mt-4 w-">
                {items.length === 0 ? 
                <div className="col-span-2 flex justify-center ">You have no products in cart</div>:
                <div className="col-span-2 flex flex-col justify-center gap-4">
        {items.map((value , index) =>{
            return(
                
                <div className="bg-white rounded-lg grid grid-cols-3 mx-9 p-4"
                        key={index}>
                    <div> 
                    <img src={value.image}/>
                    </div>
                    <div className="col-span-2 mx-2 ">
                        <div className="flex justify-around">
                            <p className=" font-semibold text-2xl">
                                {value.name}
                            </p>
                        <CustomButton onClick={() => handleRemove(index)}
                                            className = 'w-8'
                                            value = 'xoa' />
                                            
                        </div>
                        <div className="my-4">
                            <p>{value.description}</p>
                        </div>
                        <div className="flex justify-around "
                           >
                            <div style={{backgroundColor :'#e5e7eb'}}>
                            <CustomButton onClick={handleMinus}
                                            className = 'w-8 text-red-600'
                                            value = '-' />
                            <span  className='w-4 '>{value.quantity}</span>
                            <CustomButton onClick={handlePlus(value.quantity)}
                                            className='w-8 text-red-600'
                                            value = '+'/>
                            </div>
                            <p className=" font-semibold text-2xl">
                                {value.totalPrice}
                            </p>
      
                        </div>

                    </div>
                </div>
            )
        })}
      </div>}
      
